package day01;

public class 타입_실수 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// double 실수를 담는다.
		
		double a = 10.4;
		double b = 3.3;
		double c = a + b;
		double d = a - b;
		double e = a * b;
		double f = a / b;
		double g = a % b;
		
		// home 앞으로쭉  end 뒤로쭉   편한 단축키
		
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
		System.out.println(f);
		System.out.println(g);
		
		
		
	}

}
